    private void MMHSMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_MMHSMouseClicked
        if(fm==null || fm.isClosed() ){
            fm = new FMhs();
            dskutama.add(fm);
            fm.setLocation(this.getWidth()/2 - fm.getWidth()/2, this.getHeight()/2-fm.getHeight()/2);
            fm.setVisible(true);
            fd.dispose();
            fmat.dispose();
        }else{
            fm.setVisible(true);
        }
    }//GEN-LAST:event_MMHSMouseClicked

