    private void MDosenMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_MDosenMouseClicked
        if(fd==null || fd.isClosed() ){
            fd = new FDosen();
            dskutama.add(fd);
            fd.setLocation(this.getWidth()/2 - fd.getWidth()/2, this.getHeight()/2-fd.getHeight()/2);
            fd.setVisible(true);
            fm.dispose();
            fmat.dispose();
        }else{
            fd.setVisible(true);
        }
    }//GEN-LAST:event_MDosenMouseClicked

    private void MMatkulMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_MMatkulMouseClicked
        if(fmat==null || fmat.isClosed() ){
            fmat = new FMatkul();
            dskutama.add(fmat);
            fmat.setLocation(this.getWidth()/2 - fmat.getWidth()/2, this.getHeight()/2-fmat.getHeight()/2);
            fmat.setVisible(true);
            fd.dispose();
            fm.dispose();
        }else{
            fm.setVisible(true);
        }
    }//GEN-LAST:event_MMatkulMouseClicked

